mapper.select:
select 
from flash_activity


        activity_name like concat('%', #{activityName}, '%')


        and activity_desc like concat('%', #{activityDesc}, '%')


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


order by create_time desc
limit #{offset}, #{pageSize}

select count(*)
from flash_activity


        activity_name like concat('%', #{activityName}, '%')


        and activity_desc like concat('%', #{activityDesc}, '%')


        and start_time = #{startTime}


        and end_time = #{endTime}


        and status = #{status}


limit #{offset}, #{pageSize}
